import React, { useState } from 'react';

const FileUpload = () => {
  const [file, setFile] = useState(null); // Store selected file
  const [message, setMessage] = useState(''); // Store server response message

  // Function to handle file selection
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  // Function to upload the file to the backend
  const handleFileUpload = async () => {
    if (!file) {
      setMessage('Please select a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:3000/upload', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        setMessage('File uploaded successfully!');
      } else {
        setMessage(data.message || 'Error uploading file');
      }
    } catch (error) {
      setMessage('Error uploading file');
      console.error(error);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="w-96 p-6 bg-white rounded-lg shadow-lg">
        <h1 className="text-xl font-semibold mb-4 text-center">Upload Your Resume</h1>

        {/* File input */}
        <input
          type="file"
          onChange={handleFileChange}
          accept=".pdf"
          className="block w-full p-2 mb-4 border border-gray-300 rounded text-sm"
        />

        <button
          onClick={handleFileUpload}
          className="w-full p-2 bg-blue-500 text-white rounded font-semibold hover:bg-blue-600 transition"
        >
          Upload Resume
        </button>

        {message && <p className="mt-4 text-center text-gray-700">{message}</p>}
      </div>
    </div>
  );
};

export default FileUpload;
